import React, { useContext } from "react";
import { StudentsContext } from "../context/StudentsContext";
import { makeStyles } from "@material-ui/core/styles";
import Popper from "@material-ui/core/Popper";
import Typography from "@material-ui/core/Typography";
import Fade from "@material-ui/core/Fade";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles((theme) => ({
  root: {
    width: 500,
  },
  attending: {
    cursor: "pointer",
  },
  absent: {
    opacity: 0.3,
    textDecoration: "line-through",
    cursor: "pointer",
  },
}));

export default function PositionedPopper(props) {
  const { isOpen, anchorEl, selectedEvent } = props;
  const classes = useStyles();
  // Create hash table for students in lesson to reduce lookup time
  const attendant_ids = {};
  selectedEvent.attendants.map((a) => {
    attendant_ids._id = a._id;
  });

  const toggleAttendance = (e, target) => {
    e.preventDefault();
    console.log(target);
  };

  return (
    <div className={classes.root}>
      <Popper
        open={isOpen}
        anchorEl={anchorEl}
        placement={"left-end"}
        transition
      >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={350}>
            <Paper>
              {selectedEvent.students &&
                selectedEvent.students.map((s) => (
                  <Typography
                    key={`student number ${s._id}`}
                    className={
                      s._id in attendant_ids
                        ? classes.attending
                        : classes.absent
                    }
                    onClick={toggleAttendance}
                  >
                    {s.givenName}
                  </Typography>
                ))}
            </Paper>
          </Fade>
        )}
      </Popper>
    </div>
  );
}
