import moment from "moment";

function validateRoom(events, room, start, duration) {
  return events.every(event => {
    if (
      testDateOverlap(
        [event.start, event.end],
        [
          moment(new Date(start)),
          moment(
            moment(new Date(start))
              .add(duration, "m")
              .toDate()
          )
        ]
      )
    ) {
      return parseInt(event.room) !== parseInt(room);
    }
    return true;
  });
}

function validateTeacher(events, teacher, start, duration) {
  return events.every(event => {
    if (
      testDateOverlap(
        [event.start, event.end],
        [
          moment(new Date(start)),
          moment(
            moment(new Date(start))
              .add(duration, "m")
              .toDate()
          )
        ]
      )
    ) {
      return parseInt(event.resourceId) !== parseInt(teacher);
    }
    return true;
  });
}

function testDateOverlap(dateArr, testDateArr) {
  if (
    testDateArr[0].isBetween(dateArr[0], dateArr[1], null, "[]") ||
    testDateArr[1].isBetween(dateArr[0], dateArr[1], null, "[]")
  ) {
    return true;
  }
}

function validateRoomAndResource(
  events,
  event,
  resourceId,
  start,
  setSelectedEvent,
  handleToggleSnackbar
) {
  const idx = events.indexOf(event);
  const otherEvents = [...events.slice(0, idx), ...events.slice(idx + 1)];
  if (!validateRoom(otherEvents, event.room, start, parseInt(event.duration))) {
    setSelectedEvent(event);
    handleToggleSnackbar("Room Conflict. Please choose another room or time");
    return 1;
  }
  if (
    !validateTeacher(otherEvents, resourceId, start, parseInt(event.duration))
  ) {
    setSelectedEvent(event);
    handleToggleSnackbar(
      "Teacher Conflict. Please choose another teacher or time"
    );
    return 1;
  }
}

export { validateRoomAndResource };
