import React, { useContext, useEffect } from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import Login from "../components/Login";
import SignUp from "../components/SignUpForm";
import Schedule from "../components/Schedule";
import { UserContext } from "../context/UserContext";
import loadUser from "../reducers/loadUserReducer";

function Routes() {
  const { user, dispatch } = useContext(UserContext);
  useEffect(() => {
    loadUser(user, dispatch);
  }, [dispatch]);

  return (
    <Switch>
      <RouteRegistration path="/signup" exact component={SignUp} />
      <RouteRegistration path="/login" exact component={Login} />
      <RouteProtected path="/" component={Schedule} />
    </Switch>
  );
}

const RouteRegistration = ({ component: Component, ...rest }) => {
  const { user } = useContext(UserContext);

  return (
    <Route
      {...rest}
      render={(props) =>
        !user.isAuthenticated ? (
          <Component {...props} />
        ) : (
          <Redirect to="/schedule" />
        )
      }
    />
  );
};

const RouteProtected = ({ component: Component, ...rest }) => {
  const { user } = useContext(UserContext);
  return (
    <Route
      {...rest}
      render={(props) =>
        user.isAuthenticated ? (
          <Component {...props} />
        ) : (
          <Redirect to="/login" />
        )
      }
    />
  );
};

export default Routes;
