import React, { useContext } from "react";
import moment from "moment";
import { TeachersContext } from "../context/TeachersContext";
import AuthAPI from "../utils/AuthAPI";
import IconButton from "@material-ui/core/IconButton";
import RightArrowIcon from "@material-ui/icons/ChevronRight";
import LeftArrowIcon from "@material-ui/icons/ChevronLeft";
import { withStyles } from "@material-ui/styles";
import styles from "../styles/CustomToolbarStyles";
import "react-big-calendar/lib/sass/toolbar.scss";

const CustomToolbar = withStyles(theme => styles)(props => {
  const {
    classes,
    onNavigate,
    onView,
    isRTL,
    date,
    handleBtnClick,
    handlePayrollBtnClick
  } = props;
  const { teachers } = useContext(TeachersContext);
  const authAPI = useContext(AuthAPI);

  const handleLogout = e => {
    e.preventDefault();
    console.log("Logged out");
  };

  return (
    <div className={classes.toolbar}>
      <div className={classes.btnGroup}>
        <button
          className={classes.navBtn}
          type="button"
          onClick={() => onView("week")}
        >
          Week
        </button>
        <button
          className={classes.navBtn}
          type="button"
          onClick={() => onView("day")}
        >
          Day
        </button>
      </div>
      <div className={classes.btnGroup}>
        <IconButton
          className={classes.navBtn}
          type="button"
          onClick={() => onNavigate("PREV")}
        >
          <LeftArrowIcon
            className={classes.navIcon}
            icon={isRTL ? "chevron-right" : "chevron-left"}
          />
        </IconButton>
        <span
          className={classes.todayLabel}
          onClick={() => onNavigate("TODAY")}
        >
          {moment(date)
            .format("dddd MM/DD")
            .toLocaleString()}
        </span>
        <IconButton
          className={classes.navBtn}
          type="button"
          onClick={() => onNavigate("NEXT")}
        >
          <RightArrowIcon
            className={classes.navIcon}
            icon={isRTL ? "chevron-left" : "chevron-right"}
          />
        </IconButton>
      </div>
      <div>
        <ul className={classes.teacherList}>
          {teachers.map(t => (
            <li
              className={
                t.overThresholdTwoMins > 0
                  ? classes.listItemRed
                  : t.overThresholdOneMins > 0
                  ? classes.listItemYellow
                  : classes.listItem
              }
              key={t.resourceId}
            >{`${t.name}: ${t.teachingMins}`}</li>
          ))}
        </ul>
      </div>
      <div className={classes.btnGroup}>
        <button className={classes.navBtn} onClick={handleBtnClick}>
          New Teacher
        </button>
      </div>
      <div className={classes.btnGroup}>
        <button className={classes.navBtn} onClick={handlePayrollBtnClick}>
          Payroll
        </button>
      </div>
      <div className={classes.btnGroup}>
        <button className={classes.logoutBtn} onClick={handlePayrollBtnClick}>
          Logout
        </button>
      </div>
    </div>
  );
});

export default CustomToolbar;
