import React, { createContext, useReducer } from "react";
import useTeachersState from "../hooks/useTeachersState";
import axios from "axios";
import teachersReducer from "../reducers/teachersReducer";

const defaultTeachers = async () => {
  let teachersArr;
  await axios
    .get("/teachers/")
    .then(res => {
      teachersArr = res.data;
    })
    .catch(err => console.log(err));
  console.log(teachersArr);
  return teachersArr;
};

export const TeachersContext = createContext();

export function TeachersProvider(props) {
  const [teachers, dispatch] = useReducer(teachersReducer, defaultTeachers);
  // const teachers = useTeachersState(defaultTeachers);

  return (
    <TeachersContext.Provider value={{ teachers, dispatch }}>
      {props.children}
    </TeachersContext.Provider>
  );
}
