import { useState } from "react";
import axios from "axios";
import moment from "moment";
import { getRecurrences } from "../helperFunctions";

export default function(initialEvents) {
  const [events, setEvents] = useState(initialEvents);
  const JapaneseHolidays = require("japanese-holidays");

  const addEvent = async function(event) {
    const newEvents = [];
    if (event.recur === true) {
      const recurrences = getRecurrences(event);
      recurrences.map(r => {
        const newEvent = {
          ...event,
          start: r,
          end: moment(r)
            .add(event.duration, "m")
            .toDate(),
          isNewEvent: false
        };
        if (JapaneseHolidays.isHoliday(r)) {
          newEvent.isHoliday = true;
        }
        newEvents.push(newEvent);
        newEvents[newEvents.length - 1].isLast = true;
      });
    }
    if (JapaneseHolidays.isHoliday(event.start)) {
      event = { ...event, isHoliday: true };
    }
    event = { ...event, isNewEvent: true };
    newEvents.unshift(event);
    setEvents([...events, ...newEvents]);

    return await axios
      .post("/lessons/add", newEvents)
      .then(res => console.log(res.data))
      .catch(err => console.log(err));
  };

  const testObj = {
    events,
    getEvents: async function(dateTime) {
      return await axios
        .get("/lessons/")
        .then(res => {
          if (res.data.length > 0) {
            res.data.map(event => {
              event.start = new Date(event.start);
              event.end = new Date(event.end);
              console.log("Is Last: ", event.isLast);
              if (event.isLast) {
                console.log(event.start.getTime(), dateTime);
                // if (1585113900000 >= event.start.getTime()) {
                //   addEvent(event);
                // }
              }
            });
            setEvents([...res.data, events[0]]);
          }
        })
        .catch(err => console.log(err));
    },
    addEvent: async function(event) {
      const newEvents = [];
      if (event.recur === true) {
        const recurrences = getRecurrences(event);
        recurrences.map(r => {
          const newEvent = {
            ...event,
            start: r,
            end: moment(r)
              .add(event.duration, "m")
              .toDate(),
            isNewEvent: false
          };
          if (JapaneseHolidays.isHoliday(r)) {
            newEvent.isHoliday = true;
          }
          newEvents.push(newEvent);
          newEvents[newEvents.length - 1].isLast = true;
        });
      }
      if (JapaneseHolidays.isHoliday(event.start)) {
        event = { ...event, isHoliday: true };
      }
      event = { ...event, isNewEvent: true };
      newEvents.unshift(event);

      return await axios
        .post("/lessons/add", newEvents)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
    },
    deleteEvent: async function(event) {
      const newEvents = events.filter(evt => evt._id !== event._id);
      setEvents(newEvents);
      return await axios
        .delete(`/lessons/delete/${event._id}`)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
    },
    editEvent: async function(event, editedEvent) {
      const idx = events.findIndex(e => e._id === event._id);
      const nextEvents = [...events];

      nextEvents.splice(idx, 1, editedEvent);
      setEvents(nextEvents);

      return await axios
        .put(`/lessons/update/${event._id}`, editedEvent)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
    }
  };

  return testObj;
}
