import axios from "axios";
import { createPayPeriodData } from "../helperFunctions";
import uuid from "uuid/v4";

const reducer = async (state, action) => {
  const teacher = action.teacher;
  switch (action.type) {
    case "GET_TEACHERS":
      await axios
        .get("/teachers/")
        .then(res => {
          if (res.data.length > 0) {
            setTeachers(res.data);
          }
        })
        .catch(err => console.log(err));
      return [...state, res.data];

    case "ADD_TEACHER":
      await axios
        .post("/teachers/add", teacher)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
      return [...state, { id: uuid(), teacher }];

    case "DELETE_TEACHER":
      await axios
        .delete(`/teachers/delete/${teacher._id}`)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
      return state.filter(t => t.id !== action.id);

    case "UPDATE_TEACHER":
      await axios
        .put(`/teachers/update/${teacher._id}`, teacher)
        .then(res => console.log(res.data))
        .catch(err => console.log(err));
      return state.map(t =>
        t.id === action.id
          ? {
              ...t,
              resourceTitle: t.resourceTitle,
              name: t.name,
              familyName: t.familyName,
              teachingMins: t.teachingMins,
              holidayMins: t.holidayMins,
              outsideDutyMins: t.outsideDutyMins,
              otThreshold: t.otThreshold,
              overThresholdOneMins: t.overThresholdOneMins,
              overThresholdTwoMins: t.overThresholdTwoMins
            }
          : t
      );
    default:
      return state;
  }
};
export default reducer;
