import React, { useReducer, createContext } from "react";
import teachersReducer from "../reducers/teachersReducer";

const defaultTeachers = [];

export const TeachersContext = createContext();

export function TeachersProvider(props) {
  const [teachers, dispatch] = useReducer(teachersReducer, defaultTeachers);

  return (
    <TeachersContext.Provider value={(teachers, dispatch)}>
      {props.children}
    </TeachersContext.Provider>
  );
}
