import React, { useEffect } from "react";
import moment from "moment";
import useFormState from "../hooks/useFormState";
import { makeStyles } from "@material-ui/core/styles";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import roomList from "../rooms";

const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing(2)
  }
}));

export default function EventForm(props) {
  const classes = useStyles();

  const [title, handleTitleChange, titleReset] = useFormState("");
  const [duration, handleDurationChange, durationReset] = useFormState("");
  const [resource, handleResourceChange, resourceReset] = useFormState("");
  const [room, handleRoomChange, roomReset] = useFormState("");

  const {
    addEvent,
    isOpen,
    toggleIsOpen,
    addTeachingMins,
    events,
    teacherList,
    startTime,
    handleStartTimeChange,
    startTimeReset
  } = props;

  useEffect(() => {
    // Check whether the current value is already in the palettes list. If so, display error
    ValidatorForm.addValidationRule("isUniquePaletteName", value => {
      return true;
    });
  });

  useEffect(() => {
    durationReset();
    titleReset();
    resourceReset();
    roomReset();
    addTeachingMins();
    startTimeReset();
  }, [events]);

  const handleAddEvent = () => {
    const startTimeObj = new Date(startTime);
    addEvent({
      title: `${title} -- ${room}`,
      start: startTimeObj,
      end: moment(startTimeObj)
        .add(duration, "m")
        .toDate(),
      duration: duration,
      resourceId: parseInt(resource)
    });
  };

  return (
    <Dialog
      open={isOpen}
      onClose={toggleIsOpen}
      aria-labelledby="form-dialog-title"
    >
      <DialogTitle id="form-dialog-title">New Lesson</DialogTitle>
      <DialogContent>
        <DialogContentText>Enter Lesson Info</DialogContentText>
        <FormControl className={classes.formControl}>
          <TextField
            autoFocus
            margin="dense"
            id="title"
            label="Lesson Name"
            type="text"
            value={title}
            onChange={handleTitleChange}
            fullWidth
            validators={["required", "isUniquePaletteName"]}
            errorMessages={[
              "Enter the Lesson Name",
              "That name is already taken"
            ]}
          />
        </FormControl>
        <FormControl className={classes.formControl}>
          <TextField
            autoFocus
            margin="dense"
            id="startTime"
            label="Start Time"
            type="text"
            value={startTime}
            onChange={handleStartTimeChange}
            fullWidth
            validators={["required", "isUniquePaletteName"]}
            errorMessages={[
              "Enter the Start Time",
              "That name is already taken"
            ]}
          />
        </FormControl>
        <FormControl className={classes.formControl}>
          <TextField
            margin="dense"
            id="duration"
            label="Lesson Duration"
            type="text"
            pattern="[0-9]*"
            value={duration}
            onChange={handleDurationChange}
            fullWidth
            validators={["required", "isUniquePaletteName"]}
            errorMessages={["Enter the Duration", "That name is already taken"]}
          />
        </FormControl>
        <FormControl className={classes.formControl}>
          <InputLabel htmlFor="resource">Teacher</InputLabel>
          <Select
            native
            id="resource"
            value={resource}
            onChange={handleResourceChange}
            name="resource"
            validators={["required", "isUniquePaletteName"]}
            errorMessages={[
              "Please enter a palette name",
              "That name is already taken"
            ]}
          >
            <option value="" />
            {teacherList.map(t => (
              <option key={`teacher-${t.resourceId}`} value={t.resourceId}>
                {t.name}
              </option>
            ))}
          </Select>
        </FormControl>
        <FormControl className={classes.formControl}>
          <InputLabel htmlFor="room">Room #</InputLabel>
          <Select
            native
            id="room"
            value={room}
            onChange={handleRoomChange}
            name="room"
            validators={["required", "isUniquePaletteName"]}
            errorMessages={[
              "Please enter a palette name",
              "That name is already taken"
            ]}
          >
            <option value="" />
            {roomList.map(r => (
              <option key={`room-${r}`} value={r}>
                {r}
              </option>
            ))}
          </Select>
        </FormControl>
      </DialogContent>
      <DialogActions>
        <Button onClick={toggleIsOpen} color="primary">
          Cancel
        </Button>
        <Button onClick={handleAddEvent} color="primary">
          Add Class
        </Button>
      </DialogActions>
    </Dialog>
  );
}
