import React from "react";

import "react-big-calendar/lib/sass/toolbar.scss";

import IconButton from "@material-ui/core/IconButton";
import RightArrowIcon from "@material-ui/icons/ChevronRight";
import LeftArrowIcon from "@material-ui/icons/ChevronLeft";

export default class CalendarToolbar extends React.Component {
  componentDidMount() {
    const view = this.props.view;
    console.log(view);
  }

  render() {
    return (
      <div>
        <div className="rbc-btn-group">
          <IconButton
            className="toolbar-navigation-button"
            type="button"
            onClick={() => this.props.onNavigate("PREV")}
          >
            <LeftArrowIcon
              className="navigate-icon"
              icon={this.props.isRTL ? "chevron-right" : "chevron-left"}
            />
          </IconButton>
          <span
            className="today-label"
            onClick={() => this.props.onNavigate("TODAY")}
          >
            {JSON.stringify(this.props.date)}
          </span>
          <IconButton
            className="toolbar-navigation-button"
            type="button"
            onClick={() => this.props.onNavigate("NEXT")}
          >
            <RightArrowIcon
              className="navigate-icon"
              icon={this.props.isRTL ? "chevron-left" : "chevron-right"}
            />
          </IconButton>
        </div>
        <div>
          <button
            className="toolbar-navigation-button"
            type="button"
            onClick={this.view.bind(null, "WorkWeek")}
          >
            Week
          </button>
          <button
            className="toolbar-navigation-button"
            type="button"
            onClick={this.view.bind(null, "day")}
          >
            Day
          </button>
        </div>
      </div>
    );
  }
}
