import React, { useState } from "react";
import { Calendar, momentLocalizer, Views } from "react-big-calendar";
import moment from "moment";
import myEventsList from "../events";
import withDragAndDrop from "react-big-calendar/lib/addons/dragAndDrop";
import { WorkWeek } from "./CustomViews";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);
const DragAndDropCalendar = withDragAndDrop(Calendar);

const Schedule = props => {
  const [events, setEvents] = useState(myEventsList);

  // Limit displayed hours of the day
  const minTime = new Date().setHours(9, 0, 0);
  const maxTime = new Date().setHours(21, 0, 0);

  return (
    <div>
      <DragAndDropCalendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        defaultView={WorkWeek}
        min={minTime}
        max={maxTime}
        style={{ width: "95vw", maxHeight: "100%" }}
      />
    </div>
  );
};
export default Schedule;
