import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import roomList from "../rooms";

const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing(2)
  }
}));

export default function EventForm(props) {
  const classes = useStyles();
  return (
    <Dialog
      open={isOpen}
      onClose={toggleIsOpen}
      aria-labelledby="form-dialog-title"
    >
      <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
      <DialogContent>
        <DialogContentText>
          Please enter the start time and class duration
        </DialogContentText>
        <FormControl className={classes.formControl}>
          <TextField
            autoFocus
            margin="dense"
            id="title"
            label="Class Name"
            type="text"
            value={title}
            onChange={handleTitleChange}
            fullWidth
            required
          />
        </FormControl>
        <FormControl className={classes.formControl}>
          <TextField
            autoFocus
            margin="dense"
            id="startTime"
            label="Start Time"
            type="text"
            value={moment(startTime).format("hh:mm")}
            onChange={setStartTime}
            fullWidth
            required
          />
        </FormControl>
        <FormControl className={classes.formControl}>
          <TextField
            margin="dense"
            id="duration"
            label="Class Duration"
            type="text"
            pattern="[0-9]*"
            value={duration}
            onChange={handleDurationChange}
            fullWidth
            required
          />
        </FormControl>
        <FormControl className={classes.formControl}>
          <InputLabel htmlFor="resource">Teacher</InputLabel>
          <Select
            native
            id="resource"
            value={resource}
            onChange={handleResourceChange}
            name="resource"
            required
          >
            <option value="" />
            {teacherList.map(t => (
              <option key={`teacher-${t.resourceId}`} value={t.resourceId}>
                {t.name}
              </option>
            ))}
          </Select>
        </FormControl>
        <FormControl className={classes.formControl}>
          <InputLabel htmlFor="room">Room #</InputLabel>
          <Select
            native
            id="room"
            value={room}
            onChange={handleRoomChange}
            name="room"
            required
          >
            <option value="" />
            {roomList.map(r => (
              <option key={`room-${r}`} value={r}>
                {r}
              </option>
            ))}
          </Select>
        </FormControl>
      </DialogContent>
      <DialogActions>
        <Button onClick={toggleIsOpen} color="primary">
          Cancel
        </Button>
        <Button onClick={handleAddEvent} color="primary">
          Add Class
        </Button>
      </DialogActions>
    </Dialog>
  );
}
