import React, { useState, useContext } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import ListItemText from "@material-ui/core/ListItemText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Dialog from "@material-ui/core/Dialog";
import PersonIcon from "@material-ui/icons/Person";
import { blue } from "@material-ui/core/colors";
import TeachersContext from "../context/TeachersContext";

const useStyles = makeStyles({
  avatar: {
    backgroundColor: blue[100],
    color: blue[600]
  }
});

const Payroll = props => {
  const classes = useStyles();

  const { teachers } = useContext(TeachersContext);
  const { currentTeacher, setCurrentTeacher } = useState("");

  const handleUpdateCurrentTeacher = teacher => {
    setCurrentTeacher(teacher.name);
  };

  return (
    <Dialog onClose={false} aria-labelledby="teacher-select-title" open={true}>
      <DialogTitle id="teacher-select-title">
        Payroll: Select a teacher
      </DialogTitle>
      <List>
        {teachers.map(t => (
          <ListItem
            button
            onClick={handleUpdateCurrentTeacher}
            key={t.resourceId}
          >
            <ListItemAvatar>
              <Avatar className={classes.avatar}>
                <PersonIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary={t.name} />
          </ListItem>
        ))}
      </List>
    </Dialog>
    //   <Dialog onClose={false} aria-labelledby={`${currentTeacher}-payroll`} open={true}>
    //   <DialogTitle id={`${currentTeacher}-payroll`}>
    //     Payroll for {currentTeacher}
    //   </DialogTitle>
    //   <List>
    //     {teachers.map(t => (
    //       <ListItem
    //         button
    //         onClick={handleUpdateCurrentTeacher(t)}
    //         key={t.resourceId}
    //       >
    //         <ListItemAvatar>
    //           <Avatar className={classes.avatar}>
    //             <PersonIcon />
    //           </Avatar>
    //         </ListItemAvatar>
    //         <ListItemText primary={currentTeacher} />
    //       </ListItem>
    //     ))}
    //   </List>
    // </Dialog>
  );
};
export default Payroll;
