import axios from "axios";

const API_URI = "http://localhost:5000";

const getTeachers = async setTeachers => {
  try {
    const res = await axios.get(`${API_URI}/teachers/`).then(res => {
      if (res.data.length > 0) {
        console.log(res.data);
        setTeachers(res.data);
      }
    });
    console.log(res);
  } catch (err) {
    console.error(err);
  }
};

const getLessons = async setEvents => {
  try {
    const res = await axios.get(`${API_URI}/lessons/`).then(res => {
      if (res.data.length > 0) {
        res.data.map(event => {
          event.start = new Date(event.start);
          event.end = new Date(event.end);
        });
        setEvents(res.data);
      }
    });
    console.log(res);
  } catch (err) {
    console.log(err);
  }
};

const addLesson = async (newEvent, setEvents) => {
  axios
    .post(`${API_URI}/lessons/add`, newEvent)
    .then(res => console.log(res.data));
  setEvents([...events, newEvent]);
};

const updateTeacher = async teacher => {
  try {
    const res = await axios.put(
      `${API_URI}/teachers/update/${teacher._id}`,
      {
        name: teacher.name,
        teachingMins: teacher.teachingMins
      },
      { headers: { "Content-Type": "application/json" } }
    );
    console.log(res.data);
  } catch (err) {
    console.error(err);
  }
};

export { getTeachers, getLessons, addLesson, updateTeacher };
