import React, { useContext } from "react";
import useInputState from "../hooks/useInputState";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import ListItemText from "@material-ui/core/ListItemText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Dialog from "@material-ui/core/Dialog";
import PersonIcon from "@material-ui/icons/Person";
import AddIcon from "@material-ui/icons/Add";
import { blue } from "@material-ui/core/colors";
import TeachersContext from "../context/TeachersContext";

const useStyles = makeStyles({
  avatar: {
    backgroundColor: blue[100],
    color: blue[600]
  }
});

const Payroll = props => {
  const classes = useStyles();

  const { teachers } = useContext(TeachersContext);
  const { currentTeacher, updateCurrentTeacher } = useState("");

  const handleUpdateCurrentTeacher = teacher => {
    updateCurrentTeacher(teacher.name);
  };

  return (
    <Dialog onClose={false} aria-labelledby="simple-dialog-title" open={true}>
      <DialogTitle id="simple-dialog-title">Payroll</DialogTitle>
      <List>
        {teachers.map(t => (
          <ListItem
            button
            onClick={handleUpdateCurrentTeacher(t)}
            key={t.resourceId}
          >
            <ListItemAvatar>
              <Avatar className={classes.avatar}>
                <PersonIcon />
              </Avatar>
            </ListItemAvatar>
            <ListItemText primary={currentTeacher} />
          </ListItem>
        ))}
      </List>
    </Dialog>
  );
};
export default Payroll;
