import React, { useState, useContext } from "react";
import TeachersContext from "../context/TeachersContext";
import PayrollSheet from "./PayrollSheet";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import ListItemText from "@material-ui/core/ListItemText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Dialog from "@material-ui/core/Dialog";
import PersonIcon from "@material-ui/icons/Person";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import { blue } from "@material-ui/core/colors";

const useStyles = makeStyles({
  avatar: {
    backgroundColor: blue[100],
    color: blue[600]
  }
});

function createData(
  date,
  month,
  teachingHours,
  outsideDutyHours,
  holidayHours,
  travelAllowance,
  travelExpenses
) {
  return {
    date,
    month,
    teachingHours,
    outsideDutyHours,
    holidayHours,
    travelAllowance,
    travelExpenses
  };
}

const Payroll = props => {
  const classes = useStyles();

  const { teachers } = useContext(TeachersContext);
  const [stage, setStage] = useState("teacherSelect");
  const [currentTeacher, setCurrentTeacher] = useState("");
  const { events } = props;
  const teacher = teachers.filter(t => t.resourceId === currentTeacher)[0];
  const now = new Date();
  const monthStart = new Date(now.getFullYear(), now.getMonth(), 1);
  const monthEnd = new Date(now.getFullYear(), now.getMonth() + 1, 0);
  const daysInMonth = moment(now).daysInMonth();
  const teachingHoursByDate = createPayPeriodData(
    events,
    teacher,
    monthStart,
    monthEnd
  );

  const rows = [];
  for (let i = 1; i <= daysInMonth; i++) {
    let newRow;
    if (teachingHoursByDate[i]) {
      newRow = createData(
        i,
        now.getMonth("MMM"),
        teachingHoursByDate[i].teachingHours,
        teachingHoursByDate[i].outsideDutyHours,
        teachingHoursByDate[i].holidayHours,
        teachingHoursByDate[i].travelAllowance,
        teachingHoursByDate[i].travelExpenses
      );
    } else {
      newRow = createData(i, now.getMonth("MMM"), 0, 0, 0, 0, 0);
    }
    rows.push(newRow);
  }

  const showPayrollSheet = teacher => {
    setCurrentTeacher(teacher.resourceId);
    setStage("payrollSheet");
  };

  const hideForm = () => {
    setStage("");
  };

  return (
    <div>
      <Dialog
        onClose={hideForm}
        aria-labelledby="teacher-select-title"
        open={stage === "teacherSelect"}
      >
        <DialogTitle id="teacher-select-title">
          Payroll: Select a teacher
        </DialogTitle>
        <List>
          {teachers.map(t => (
            <ListItem
              button
              onClick={() => showPayrollSheet(t)}
              key={t.resourceId}
            >
              <ListItemAvatar>
                <Avatar className={classes.avatar}>
                  <PersonIcon />
                </Avatar>
              </ListItemAvatar>
              <ListItemText primary={t.name} />
            </ListItem>
          ))}
        </List>
      </Dialog>
      <Dialog
        onClose={hideForm}
        aria-labelledby="payroll-sheet-dialog"
        open={stage === "payrollSheet"}
      >
        <DialogTitle id="payroll-sheet-dialog">
          Payroll for {currentTeacher}
        </DialogTitle>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Date</TableCell>
              <TableCell align="right">Month</TableCell>
              <TableCell align="right">Teaching Mins</TableCell>
              <TableCell align="right">Outside DH</TableCell>
              <TableCell align="right">Holiday Work</TableCell>
              <TableCell align="right">Travel Allowance</TableCell>
              <TableCell align="right">Travel Expenses</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map(row => (
              <TableRow key={row.date}>
                <TableCell component="th" scope="row">
                  {row.date}
                </TableCell>
                <TableCell align="right">{row.month}</TableCell>
                <TableCell align="right">{row.teachingHours}</TableCell>
                <TableCell align="right">{row.outsideDutyHours}</TableCell>
                <TableCell align="right">{row.holidayHours}</TableCell>
                <TableCell align="right">{row.travelAllowance}</TableCell>
                <TableCell align="right">{row.travelExpenses}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Dialog>
    </div>
  );
};
export default Payroll;
