import React from "react";
import { withStyles } from "@material-ui/core/styles";
// import styles from "./styles/CustomLessonEventStyles";

let hexColor = props => {
  switch (props.type) {
    case "pl":
      hexColor = "#e6ba1f";
      break;
    case "beg":
      hexColor = "#00fca5";
      break;
    case "el":
      hexColor = "#02bd7b";
      break;
    case "lint":
      hexColor = "#018256";
      break;
    case "hint":
      hexColor = "#0b8a5e";
      break;
    case "adv":
      hexColor = "#04593c";
      break;
    case "lb":
      hexColor = "#97dd1a";
      break;
    case "lg1":
      hexColor = "#ca161e";
      break;
    case "lg2":
      hexColor = "#fdc100";
      break;
    case "lg3":
      hexColor = "#0065bd";
      break;
    case "lg4":
      hexColor = "#00ae3e";
      break;
    case "lg5":
      hexColor = "#ff8d2a";
      break;
    case "lg6":
      hexColor = "#8f178e";
      break;
    case "gs":
      hexColor = "#670084";
      break;
    case "ct1":
      hexColor = "#d90000";
      break;
    case "ct2":
      hexColor = "#0076d0";
      break;
    case "ct3":
      hexColor = "#008935";
      break;
    case "prm":
      hexColor = "#7d190b";
      break;
    default:
      hexColor = "#7c9ae6";
      break;
  }
};

const styles = theme => ({
  style: {
    display: props => (props.hide ? "none" : "flex"),
    backgroundColor: hexColor,
    color: "white",
    border: 0,
    fontWeight: "600",
    justifyContent: "space-between"
  }
});

const CustomLessonEvent = ({ event }) => {
  const { classes } = styles;
  console.log(classes);
  return (
    <div className={classes.style}>
      <div>{event.title}</div>
      <span style={{ fontWeight: "600" }}>{event.room}</span>
    </div>
  );
};
export default CustomLessonEvent;
