import React, { Component } from "react";
import moment from "moment";
import { withStyles } from "@material-ui/styles";
import styles from "../styles/CustomToolbarStyles";

const LessonEvent = ({ event }) => {
  return withStyles(styles)(
    class LessonEvent extends Component {
      render() {
        const { classes } = this.props;
        return (
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              fontSize: ".6rem",
              padding: "2px",
              height: "100%",
              width: "100%"
            }}
          >
            <div>{moment(event.start).format("LT")}</div>
            <div
              style={{
                fontSize: ".7rem",
                fontWeight: "600",
                display: "flex",
                justifyContent: "space-between",
                padding: "0"
              }}
            >
              <div className={classes.title}>{event.title}</div>
              <span
                style={{
                  fontSize: ".8rem",
                  color: "#1b1c1c",
                  paddingLeft: ".1rem"
                }}
              >
                {event.room}
              </span>
            </div>
            <div>{moment(event.end).format("LT")}</div>
          </div>
        );
      }
    }
  );
};
export default LessonEvent;
