import React, { useState } from "react";
import { Calendar, momentLocalizer, Navigate } from "react-big-calendar";
import TimeGrid from "react-big-calendar/lib/TimeGrid";
import withDragAndDrop from "react-big-calendar/lib/addons/dragAndDrop";
import moment from "moment";
import * as dates from "date-arithmetic";
import myEventsList from "../events";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);
const DragAndDropCalendar = withDragAndDrop(Calendar);

const WorkWeek = () => {
  let { date } = this.props;
  let range = WorkWeek.range(date);

  return <TimeGrid {...this.props} range={range} />;
};

WorkWeek.range = date => {
  // Set start date to the current work week's Monday
  let start = date;
  let gotDate = date.getDate();
  // Check the current date's day as integer from 1(Mon) to 7(Sun)
  switch (date.getDay()) {
    case 2:
      date.setDate(gotDate - 1);
      break;

    case 3:
      date.setDate(gotDate - 2);
      break;

    case 4:
      date.setDate(gotDate - 3);
      break;

    case 5:
      date.setDate(gotDate - 4);
      break;

    case 6:
      date.setDate(gotDate - 5);
      break;

    case 7:
      date.setDate(gotDate - 6);
      break;

    default:
      break;
  }

  let end = dates.add(start, 5, "day");
  let current = start;
  let range = [];

  while (dates.lte(current, end, "day")) {
    range.push(current);
    current = dates.add(current, 1, "day");
  }
  return range;
};

// Set navigation button behavior
WorkWeek.navigate = (date, action) => {
  switch (action) {
    case Navigate.PREVIOUS:
      return dates.subtract(date, 7, "day");

    case Navigate.NEXT:
      return dates.add(date, 7, "day");

    default:
      return date;
  }
};

WorkWeek.title = date => {
  console.log(date);
  return `${date.toLocaleDateString()}`;
};

const Schedule = props => {
  const [events, setEvents] = useState(myEventsList);

  // Limit displayed hours of the day
  const minTime = new Date();
  minTime.setHours(9, 0, 0);
  const maxTime = new Date();
  maxTime.setHours(21, 0, 0);

  // Style events based on event.type
  const eventStyleGetter = event => {
    let hexColor;
    switch (event.type) {
      case "pl":
        hexColor = "309632";
        break;
      case "kids":
        hexColor = "c47900";
        break;
      case "gs":
        hexColor = "52167d";
        break;
      case "prem":
        hexColor = "781419";
        break;
      default:
        hexColor = "3767a6";
        break;
    }

    let backgroundColor = "#" + hexColor;
    let style = {
      backgroundColor: backgroundColor,
      color: "white",
      border: 0,
      display: "block"
    };
    return {
      style: style
    };
  };

  return (
    <div>
      <DragAndDropCalendar
        style={{ width: "95vw", maxHeight: "100%" }}
        localizer={localizer}
        views={{ week: WorkWeek, day: true }}
        defaultView="day"
        events={events}
        startAccessor="start"
        endAccessor="end"
        resources={[
          { teacherId: 0, teacherName: "Martin" },
          { teacherId: 1, teacherName: "Tim" },
          { teacherId: 2, teacherName: "Sarah" },
          { teacherId: 3, teacherName: "Miles" }
        ]}
        resourceIdAccessor="teacherId"
        resourceTitleAccessor="teacherName"
        selectable
        eventPropGetter={eventStyleGetter}
        step={30}
        timeslots={2}
        min={minTime}
        max={maxTime}
      />
    </div>
  );
};
export default Schedule;
