import React from "react";
import moment from "moment";

const LessonEvent = ({ event }) => (
  <div style={{ fontSize: ".5rem", padding: ".1rem" }}>
    <div>{moment(event.start).format("LT")}</div>
    <div
      style={{
        fontSize: ".7rem",
        fontWeight: "600",
        display: "flex",
        justifyContent: "space-between",
        padding: "0"
      }}
    >
      <div>{event.title}</div>
      <span style={{ color: "blue" }}>{event.room}</span>
    </div>
    <div style={{ fontSize: ".5rem" }}>{moment(event.end).format("LT")}</div>
  </div>
);
export default LessonEvent;
