import React from "react";
import { Calendar, Views, Navigate } from "react-big-calendar";
import TimeGrid from "react-big-calendar/lib/TimeGrid";
import * as dates from "date-arithmetic";

const WorkWeek = () => {
  let { date } = this.props;
  let range = WorkWeek.range(date);

  return <TimeGrid {...this.props} range={range} />;
};

WorkWeek.range = date => {
  // Set start date to the current work week's Monday
  let start = date;
  let gotDate = date.getDate();
  // Check the current date's day as integer from 1(Mon) to 7(Sun)
  switch (date.getDay()) {
    case 2:
      date.setDate(gotDate - 1);
      break;

    case 3:
      date.setDate(gotDate - 2);
      break;

    case 4:
      date.setDate(gotDate - 3);
      break;

    case 5:
      date.setDate(gotDate - 4);
      break;

    case 6:
      date.setDate(gotDate - 5);
      break;

    case 7:
      date.setDate(gotDate - 6);
      break;

    default:
      break;
  }

  let end = dates.add(start, 5, "day");
  let current = start;
  let range = [];

  while (dates.lte(current, end, "day")) {
    range.push(current);
    current = dates.add(current, 1, "day");
  }
  return range;
};

// Set navigation button behavior
WorkWeek.navigate = (date, action) => {
  switch (action) {
    case Navigate.PREVIOUS:
      return dates.subtract(date, 7, "day");

    case Navigate.NEXT:
      return dates.add(date, 7, "day");

    default:
      return date;
  }
};

WorkWeek.title = date => {
  console.log(date);
  return `${date.toLocaleDateString()}`;
};

let CustomView = ({ localizer }) => (
  <React.Fragment>
    <ExampleControlSlot.Entry waitForOutlet>
      <strong>The Calendar below implments a custom 3-day week view</strong>
    </ExampleControlSlot.Entry>
    <Calendar
      events={events}
      localizer={localizer}
      defaultView={Views.WEEK}
      defaultDate={new Date(2015, 3, 1)}
      views={{ month: true, week: WorkWeek }}
    />
  </React.Fragment>
);

export default CustomView;
